var api = require(api);

module.exports = function (req, res, options, next) {
    api.get('field').read({}, function (err, data) {
        var model = res.exposeModel('common.foo', data);
        res.exposeView('field', {
            template: 'common.foo',
            data: data,
            models: {
                roster: model
            }
        });
    });
};
